mapper.select:
select c.camping_idx, c.facltNm, c.featurenm, c.intro, c.lineintro, c.tel, c.lctcl, c.addr1, c.camping_viewcount, ci.sbrscl, cimg.first_img from camping c join camping_internal ci on c.camping_idx = ci.camping_idx join camping_img cimg on c.camping_idx = cimg.camping_idx where cimg.first_img is not null
and c.facltNm like '%${keyword}%'
and c.induty like '%${induty}%'
and c.lctcl = #{lctcl}
and c.doNm = #{firstSelect}
and c.sigunguNm = #{secondSelect}
order by c.camping_idx offset #{paging.offset} rows fetch next #{paging.perPage} rows only

select count(*) from camping where camping_idx >= 1

	and facltNm like '%${keyword}%' 


	and induty like '%${induty}%'


	and lctcl = #{lctcl}


	and doNm = #{firstSelect}

		and sigunguNm = #{secondSelect}
